"use strict";

import domain from "../domain";
import placeToEat from "../domain/placeToEat";

export default function addPlaceToEat(displaySuccess) {
    let repo = domain.getPlacesRepo();
    
    return ({ name, cuisine }) => {
        let id = Math.floor(Math.random() * 1001); // Delegate to UUID generator.
        let place = placeToEat({ id, name, cuisine });

        repo.add(place);
        displaySuccess(place);
    };
}
