"use strict";

import domain from "../domain";

// First implementation always return the same in memory hardcoded place :)
function findPlaceToEat() {
    let repo = domain.getPlacesRepo();
    return () => repo.fetchRandom();
};

// Enums. // Maybe move to their own module.
findPlaceToEat.RANDOM = "random";
findPlaceToEat.POPULAR = "popular";
findPlaceToEat.CUISINE = "cuisine";

export default findPlaceToEat;
